import React, { useState } from 'react';
import { Save, RefreshCw, Trash2, AlertTriangle } from 'lucide-react';
import { useStore } from '../store/useStore';

const Settings: React.FC = () => {
  const { user, setUser, connections, setConnections } = useStore();
  const [isLoading, setIsLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('profile');

  const handleSaveProfile = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    
    try {
      // Mock save operation
      await new Promise(resolve => setTimeout(resolve, 1000));
      alert('Profile updated successfully!');
    } catch (error) {
      console.error('Failed to save profile:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleConnectPlatform = async (platform: string) => {
    setIsLoading(true);
    
    try {
      // Mock connection process
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      const newConnection = {
        id: `conn-${platform.toLowerCase()}`,
        platform: platform as any,
        isConnected: true,
        accessToken: `mock-token-${platform.toLowerCase()}`,
        lastSyncDate: new Date()
      };

      setConnections([...connections, newConnection]);
      alert(`${platform} connected successfully!`);
    } catch (error) {
      console.error(`Failed to connect ${platform}:`, error);
      alert(`Failed to connect ${platform}`);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDisconnectPlatform = (platform: string) => {
    if (window.confirm(`Are you sure you want to disconnect ${platform}?`)) {
      setConnections(connections.filter(conn => conn.platform !== platform));
    }
  };

  const tabs = [
    { id: 'profile', name: 'Profile', icon: 'ðŸ‘¤' },
    { id: 'connections', name: 'Platform Connections', icon: 'ðŸ”—' },
    { id: 'notifications', name: 'Notifications', icon: 'ðŸ””' },
    { id: 'data', name: 'Data Management', icon: 'ðŸ’¾' }
  ];

  const platforms = ['ESPN', 'Yahoo', 'Sleeper', 'CBS'];

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Settings</h1>
        <p className="text-gray-600">Manage your account and platform connections</p>
      </div>

      {/* Tabs */}
      <div className="border-b border-gray-200">
        <nav className="-mb-px flex space-x-8">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`py-2 px-1 border-b-2 font-medium text-sm ${
                activeTab === tab.id
                  ? 'border-primary-500 text-primary-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              <span className="mr-2">{tab.icon}</span>
              {tab.name}
            </button>
          ))}
        </nav>
      </div>

      {/* Tab Content */}
      <div className="space-y-6">
        {/* Profile Tab */}
        {activeTab === 'profile' && (
          <div className="card">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Profile Information</h2>
            <form onSubmit={handleSaveProfile} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Full Name
                  </label>
                  <input
                    type="text"
                    defaultValue={user?.name || ''}
                    className="input"
                    placeholder="Your full name"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Email Address
                  </label>
                  <input
                    type="email"
                    defaultValue={user?.email || ''}
                    className="input"
                    placeholder="your@email.com"
                  />
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Default View
                </label>
                <select className="input">
                  <option value="dashboard">Dashboard</option>
                  <option value="lineups">Lineups</option>
                  <option value="stats">Statistics</option>
                </select>
              </div>
              <div className="flex justify-end">
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={isLoading}
                >
                  <Save className="h-4 w-4 mr-2" />
                  {isLoading ? 'Saving...' : 'Save Changes'}
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Connections Tab */}
        {activeTab === 'connections' && (
          <div className="space-y-6">
            <div className="card">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">Platform Connections</h2>
              <p className="text-gray-600 mb-6">
                Connect your fantasy football accounts to sync lineups and data automatically.
              </p>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {platforms.map((platform) => {
                  const connection = connections.find(conn => conn.platform === platform);
                  const isConnected = connection?.isConnected || false;
                  
                  return (
                    <div key={platform} className="border border-gray-200 rounded-lg p-4">
                      <div className="flex items-center justify-between mb-3">
                        <div className="flex items-center">
                          <div className={`w-3 h-3 rounded-full mr-3 ${
                            platform === 'ESPN' ? 'bg-fantasy-espn' :
                            platform === 'Yahoo' ? 'bg-fantasy-yahoo' :
                            platform === 'Sleeper' ? 'bg-fantasy-sleeper' :
                            'bg-fantasy-cbs'
                          }`}></div>
                          <h3 className="font-medium text-gray-900">{platform}</h3>
                        </div>
                        <span className={`badge ${isConnected ? 'badge-success' : 'badge-warning'}`}>
                          {isConnected ? 'Connected' : 'Not Connected'}
                        </span>
                      </div>
                      
                      {isConnected ? (
                        <div className="space-y-2">
                          <p className="text-sm text-gray-600">
                            Last synced: {connection?.lastSyncDate ? 
                              new Date(connection.lastSyncDate).toLocaleDateString() : 'Never'
                            }
                          </p>
                          <div className="flex space-x-2">
                            <button
                              onClick={() => {/* Sync logic */}}
                              className="btn btn-secondary text-sm flex-1"
                              disabled={isLoading}
                            >
                              <RefreshCw className="h-4 w-4 mr-1" />
                              Sync Now
                            </button>
                            <button
                              onClick={() => handleDisconnectPlatform(platform)}
                              className="btn btn-danger text-sm flex-1"
                            >
                              <Trash2 className="h-4 w-4 mr-1" />
                              Disconnect
                            </button>
                          </div>
                        </div>
                      ) : (
                        <div>
                          <p className="text-sm text-gray-600 mb-3">
                            Connect your {platform} account to sync lineups and data.
                          </p>
                          <button
                            onClick={() => handleConnectPlatform(platform)}
                            className="btn btn-primary w-full"
                            disabled={isLoading}
                          >
                            {isLoading ? 'Connecting...' : `Connect ${platform}`}
                          </button>
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            </div>

            <div className="card">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Sync Status</h3>
              <div className="space-y-3">
                {connections.filter(conn => conn.isConnected).map((connection) => (
                  <div key={connection.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <div className="flex items-center">
                      <div className={`w-2 h-2 rounded-full mr-3 ${
                        connection.platform === 'ESPN' ? 'bg-fantasy-espn' :
                        connection.platform === 'Yahoo' ? 'bg-fantasy-yahoo' :
                        connection.platform === 'Sleeper' ? 'bg-fantasy-sleeper' :
                        'bg-fantasy-cbs'
                      }`}></div>
                      <span className="font-medium text-gray-900">{connection.platform}</span>
                    </div>
                    <div className="flex items-center space-x-4">
                      <span className="text-sm text-gray-500">
                        Last sync: {connection.lastSyncDate ? 
                          new Date(connection.lastSyncDate).toLocaleString() : 'Never'
                        }
                      </span>
                      <button
                        onClick={() => {/* Sync logic */}}
                        className="text-primary-600 hover:text-primary-700 text-sm"
                      >
                        Sync Now
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Notifications Tab */}
        {activeTab === 'notifications' && (
          <div className="card">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Notification Preferences</h2>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="font-medium text-gray-900">Email Notifications</h3>
                  <p className="text-sm text-gray-600">Receive email updates about your teams</p>
                </div>
                <label className="relative inline-flex items-center cursor-pointer">
                  <input type="checkbox" className="sr-only peer" defaultChecked />
                  <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
                </label>
              </div>
              
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="font-medium text-gray-900">Injury Alerts</h3>
                  <p className="text-sm text-gray-600">Get notified when your players are injured</p>
                </div>
                <label className="relative inline-flex items-center cursor-pointer">
                  <input type="checkbox" className="sr-only peer" defaultChecked />
                  <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
                </label>
              </div>
              
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="font-medium text-gray-900">Lineup Reminders</h3>
                  <p className="text-sm text-gray-600">Reminders to set your lineup before games</p>
                </div>
                <label className="relative inline-flex items-center cursor-pointer">
                  <input type="checkbox" className="sr-only peer" defaultChecked />
                  <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-primary-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-primary-600"></div>
                </label>
              </div>
            </div>
          </div>
        )}

        {/* Data Management Tab */}
        {activeTab === 'data' && (
          <div className="space-y-6">
            <div className="card">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">Data Management</h2>
              <div className="space-y-4">
                <div className="flex items-center justify-between p-4 border border-gray-200 rounded-lg">
                  <div>
                    <h3 className="font-medium text-gray-900">Export Data</h3>
                    <p className="text-sm text-gray-600">Download all your team data as JSON</p>
                  </div>
                  <button className="btn btn-secondary">
                    Export Data
                  </button>
                </div>
                
                <div className="flex items-center justify-between p-4 border border-red-200 rounded-lg bg-red-50">
                  <div>
                    <h3 className="font-medium text-red-900">Delete Account</h3>
                    <p className="text-sm text-red-600">Permanently delete your account and all data</p>
                  </div>
                  <button className="btn btn-danger">
                    Delete Account
                  </button>
                </div>
              </div>
            </div>

            <div className="card">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Data Usage</h3>
              <div className="space-y-3">
                <div className="flex justify-between">
                  <span className="text-sm text-gray-600">Teams</span>
                  <span className="text-sm font-medium text-gray-900">{user?.teams.length || 0}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-sm text-gray-600">Platform Connections</span>
                  <span className="text-sm font-medium text-gray-900">{connections.length}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-sm text-gray-600">Last Updated</span>
                  <span className="text-sm font-medium text-gray-900">
                    {new Date().toLocaleDateString()}
                  </span>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Settings;
