import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  Users, 
  ClipboardList, 
  TrendingUp, 
  AlertTriangle,
  Plus,
  RefreshCw
} from 'lucide-react';
import { useStore } from '../store/useStore';
import { FantasyTeam } from '../types';

const Dashboard: React.FC = () => {
  const { 
    teams, 
    leagueRecords, 
    isLoading, 
    setLoading, 
    syncTeam,
    calculateWinLoss 
  } = useStore();

  useEffect(() => {
    // Load initial data
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    setLoading(true);
    try {
      // Mock data loading
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Mock teams data
      const mockTeams: FantasyTeam[] = [
        {
          id: 'team-1',
          name: 'My ESPN Team',
          platform: 'ESPN',
          leagueId: 'espn-league-1',
          leagueName: 'ESPN League',
          ownerId: 'user-1',
          record: { wins: 5, losses: 2, ties: 0 },
          season: 2024,
          isActive: true,
          lastSyncDate: new Date()
        },
        {
          id: 'team-2',
          name: 'My Yahoo Team',
          platform: 'Yahoo',
          leagueId: 'yahoo-league-1',
          leagueName: 'Yahoo League',
          ownerId: 'user-1',
          record: { wins: 3, losses: 4, ties: 0 },
          season: 2024,
          isActive: true,
          lastSyncDate: new Date()
        },
        {
          id: 'team-3',
          name: 'My Sleeper Team',
          platform: 'Sleeper',
          leagueId: 'sleeper-league-1',
          leagueName: 'Sleeper League',
          ownerId: 'user-1',
          record: { wins: 4, losses: 3, ties: 0 },
          season: 2024,
          isActive: true,
          lastSyncDate: new Date()
        }
      ];

      useStore.getState().setTeams(mockTeams);
      calculateWinLoss();
    } catch (error) {
      console.error('Failed to load dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const totalWins = teams.reduce((sum, team) => sum + team.record.wins, 0);
  const totalLosses = teams.reduce((sum, team) => sum + team.record.losses, 0);
  const totalTies = teams.reduce((sum, team) => sum + team.record.ties, 0);
  const winPercentage = totalWins / (totalWins + totalLosses + totalTies) || 0;

  const getPlatformColor = (platform: string) => {
    switch (platform) {
      case 'ESPN': return 'text-fantasy-espn';
      case 'Yahoo': return 'text-fantasy-yahoo';
      case 'Sleeper': return 'text-fantasy-sleeper';
      case 'CBS': return 'text-fantasy-cbs';
      default: return 'text-gray-600';
    }
  };

  const getRecordColor = (wins: number, losses: number) => {
    const winRate = wins / (wins + losses);
    if (winRate >= 0.6) return 'text-green-600';
    if (winRate >= 0.4) return 'text-yellow-600';
    return 'text-red-600';
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <RefreshCw className="h-8 w-8 animate-spin text-primary-600" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
          <p className="text-gray-600">Overview of all your fantasy football teams</p>
        </div>
        <div className="flex space-x-3">
          <button
            onClick={loadDashboardData}
            className="btn btn-secondary flex items-center"
          >
            <RefreshCw className="h-4 w-4 mr-2" />
            Refresh
          </button>
          <Link to="/teams" className="btn btn-primary flex items-center">
            <Plus className="h-4 w-4 mr-2" />
            Add Team
          </Link>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="card">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <Users className="h-8 w-8 text-primary-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Total Teams</p>
              <p className="text-2xl font-bold text-gray-900">{teams.length}</p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <TrendingUp className="h-8 w-8 text-green-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Total Wins</p>
              <p className="text-2xl font-bold text-gray-900">{totalWins}</p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <AlertTriangle className="h-8 w-8 text-red-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Total Losses</p>
              <p className="text-2xl font-bold text-gray-900">{totalLosses}</p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <ClipboardList className="h-8 w-8 text-blue-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Win Rate</p>
              <p className="text-2xl font-bold text-gray-900">
                {(winPercentage * 100).toFixed(1)}%
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Teams Overview */}
      <div className="card">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-lg font-semibold text-gray-900">Your Teams</h2>
          <Link to="/teams" className="text-primary-600 hover:text-primary-700 text-sm font-medium">
            View all teams
          </Link>
        </div>

        {teams.length === 0 ? (
          <div className="text-center py-8">
            <Users className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No teams yet</h3>
            <p className="text-gray-500 mb-4">Get started by adding your first fantasy team</p>
            <Link to="/teams" className="btn btn-primary">
              Add Your First Team
            </Link>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {teams.map((team) => (
              <div key={team.id} className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
                <div className="flex items-center justify-between mb-3">
                  <h3 className="font-medium text-gray-900">{team.name}</h3>
                  <span className={`text-sm font-medium ${getPlatformColor(team.platform)}`}>
                    {team.platform}
                  </span>
                </div>
                <p className="text-sm text-gray-600 mb-3">{team.leagueName}</p>
                <div className="flex items-center justify-between">
                  <span className={`text-sm font-medium ${getRecordColor(team.record.wins, team.record.losses)}`}>
                    {team.record.wins}-{team.record.losses}
                    {team.record.ties > 0 && `-${team.record.ties}`}
                  </span>
                  <button
                    onClick={() => syncTeam(team.id)}
                    className="text-xs text-primary-600 hover:text-primary-700"
                  >
                    Sync
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="card">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h3>
          <div className="space-y-3">
            <Link to="/lineups" className="flex items-center p-3 rounded-lg border border-gray-200 hover:bg-gray-50">
              <ClipboardList className="h-5 w-5 text-primary-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">Manage Lineups</p>
                <p className="text-sm text-gray-500">Set your starting lineups</p>
              </div>
            </Link>
            <Link to="/stats" className="flex items-center p-3 rounded-lg border border-gray-200 hover:bg-gray-50">
              <TrendingUp className="h-5 w-5 text-green-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">View Statistics</p>
                <p className="text-sm text-gray-500">Track your performance</p>
              </div>
            </Link>
          </div>
        </div>

        <div className="card">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Recent Activity</h3>
          <div className="space-y-3">
            <div className="flex items-center text-sm text-gray-600">
              <div className="w-2 h-2 bg-green-500 rounded-full mr-3"></div>
              ESPN team synced successfully
            </div>
            <div className="flex items-center text-sm text-gray-600">
              <div className="w-2 h-2 bg-blue-500 rounded-full mr-3"></div>
              Lineup updated for Week 8
            </div>
            <div className="flex items-center text-sm text-gray-600">
              <div className="w-2 h-2 bg-yellow-500 rounded-full mr-3"></div>
              Yahoo team needs attention
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
