import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { 
  Users, 
  TrendingUp, 
  AlertTriangle,
  Plus,
  RefreshCw,
  Settings,
  Trophy,
  Calendar,
  Activity,
  Edit3
} from 'lucide-react';
import { useStore } from '../store/useStore';
import { FantasyTeam, Lineup, Player } from '../types';

const Dashboard: React.FC = () => {
  const { 
    teams, 
    isLoading, 
    setLoading, 
    syncTeam,
    calculateWinLoss 
  } = useStore();

  const [currentWeek, setCurrentWeek] = useState(8);
  const [teamLineups, setTeamLineups] = useState<{[teamId: string]: Lineup}>({});

  useEffect(() => {
    loadDashboardData();
  }, []);

  useEffect(() => {
    if (teams.length > 0) {
      loadAllLineups();
    }
  }, [teams, currentWeek]);

  const loadDashboardData = async () => {
    setLoading(true);
    try {
      // Mock teams data
      const mockTeams: FantasyTeam[] = [
        {
          id: 'team-1',
          name: 'My ESPN Team',
          platform: 'ESPN',
          leagueId: 'espn-league-1',
          leagueName: 'ESPN League',
          ownerId: 'user-1',
          record: { wins: 5, losses: 2, ties: 0 },
          season: 2024,
          isActive: true,
          lastSyncDate: new Date()
        },
        {
          id: 'team-2',
          name: 'My Yahoo Team',
          platform: 'Yahoo',
          leagueId: 'yahoo-league-1',
          leagueName: 'Yahoo League',
          ownerId: 'user-1',
          record: { wins: 3, losses: 4, ties: 0 },
          season: 2024,
          isActive: true,
          lastSyncDate: new Date()
        },
        {
          id: 'team-3',
          name: 'My Sleeper Team',
          platform: 'Sleeper',
          leagueId: 'sleeper-league-1',
          leagueName: 'Sleeper League',
          ownerId: 'user-1',
          record: { wins: 4, losses: 3, ties: 0 },
          season: 2024,
          isActive: true,
          lastSyncDate: new Date()
        }
      ];

      useStore.getState().setTeams(mockTeams);
      calculateWinLoss();
    } catch (error) {
      console.error('Failed to load dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadAllLineups = async () => {
    const lineups: {[teamId: string]: Lineup} = {};
    
    for (const team of teams) {
      // Mock lineup data for each team
      const mockLineup: Lineup = {
        id: `lineup-${team.id}-${currentWeek}`,
        teamId: team.id,
        week: currentWeek,
        season: new Date().getFullYear(),
        players: {
          QB: { id: 'qb-1', name: 'Josh Allen', position: 'QB', team: 'BUF', projectedPoints: 24.5, injuryStatus: 'healthy' },
          RB1: { id: 'rb-1', name: 'Christian McCaffrey', position: 'RB', team: 'SF', projectedPoints: 18.2, injuryStatus: 'healthy' },
          RB2: { id: 'rb-2', name: 'Derrick Henry', position: 'RB', team: 'TEN', projectedPoints: 15.8, injuryStatus: 'questionable' },
          WR1: { id: 'wr-1', name: 'Cooper Kupp', position: 'WR', team: 'LAR', projectedPoints: 16.4, injuryStatus: 'healthy' },
          WR2: { id: 'wr-2', name: 'Davante Adams', position: 'WR', team: 'LV', projectedPoints: 14.7, injuryStatus: 'healthy' },
          TE: { id: 'te-1', name: 'Travis Kelce', position: 'TE', team: 'KC', projectedPoints: 12.3, injuryStatus: 'healthy' },
          FLEX: { id: 'wr-3', name: 'Tyreek Hill', position: 'WR', team: 'MIA', projectedPoints: 13.9, injuryStatus: 'healthy' },
          K: { id: 'k-1', name: 'Justin Tucker', position: 'K', team: 'BAL', projectedPoints: 8.5, injuryStatus: 'healthy' },
          DEF: { id: 'def-1', name: 'Buffalo Bills', position: 'DEF', team: 'BUF', projectedPoints: 7.2, injuryStatus: 'healthy' },
          BENCH: [
            { id: 'rb-3', name: 'Saquon Barkley', position: 'RB', team: 'NYG', projectedPoints: 12.1, injuryStatus: 'healthy' },
            { id: 'wr-4', name: 'Stefon Diggs', position: 'WR', team: 'BUF', projectedPoints: 11.8, injuryStatus: 'healthy' },
            { id: 'te-2', name: 'Mark Andrews', position: 'TE', team: 'BAL', projectedPoints: 9.4, injuryStatus: 'healthy' }
          ]
        },
        totalProjectedPoints: 131.9,
        lastUpdated: new Date()
      };
      
      lineups[team.id] = mockLineup;
    }
    
    setTeamLineups(lineups);
  };

  const getPlatformColor = (platform: string) => {
    switch (platform) {
      case 'ESPN': return 'bg-fantasy-espn';
      case 'Yahoo': return 'bg-fantasy-yahoo';
      case 'Sleeper': return 'bg-fantasy-sleeper';
      case 'CBS': return 'bg-fantasy-cbs';
      default: return 'bg-gray-600';
    }
  };

  const getInjuryStatus = (player: Player) => {
    if (!player.injuryStatus || player.injuryStatus === 'healthy') return null;
    
    const statusColors = {
      questionable: 'text-yellow-600 bg-yellow-100',
      doubtful: 'text-orange-600 bg-orange-100',
      out: 'text-red-600 bg-red-100'
    };

    return (
      <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${statusColors[player.injuryStatus]}`}>
        <AlertTriangle className="h-3 w-3 mr-1" />
        {player.injuryStatus.toUpperCase()}
      </span>
    );
  };

  const totalWins = teams.reduce((sum, team) => sum + team.record.wins, 0);
  const totalLosses = teams.reduce((sum, team) => sum + team.record.losses, 0);
  const totalTies = teams.reduce((sum, team) => sum + team.record.ties, 0);
  const winPercentage = totalWins / (totalWins + totalLosses + totalTies) || 0;

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <RefreshCw className="h-8 w-8 animate-spin text-primary-600" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Fantasy Central</h1>
          <p className="text-gray-600">All your fantasy teams in one place</p>
        </div>
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-2">
            <button
              onClick={() => setCurrentWeek(Math.max(1, currentWeek - 1))}
              className="btn btn-secondary"
              disabled={currentWeek <= 1}
            >
              <Calendar className="h-4 w-4" />
            </button>
            <span className="text-sm font-medium text-gray-700">Week {currentWeek}</span>
            <button
              onClick={() => setCurrentWeek(Math.min(18, currentWeek + 1))}
              className="btn btn-secondary"
              disabled={currentWeek >= 18}
            >
              <Calendar className="h-4 w-4" />
            </button>
          </div>
          <button
            onClick={loadDashboardData}
            className="btn btn-secondary flex items-center"
          >
            <RefreshCw className="h-4 w-4 mr-2" />
            Refresh All
          </button>
          <Link to="/settings" className="btn btn-primary flex items-center">
            <Settings className="h-4 w-4 mr-2" />
            Manage Teams
          </Link>
        </div>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="card">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <Users className="h-8 w-8 text-primary-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Total Teams</p>
              <p className="text-2xl font-bold text-gray-900">{teams.length}</p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <Trophy className="h-8 w-8 text-green-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Total Wins</p>
              <p className="text-2xl font-bold text-gray-900">{totalWins}</p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <Activity className="h-8 w-8 text-red-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Total Losses</p>
              <p className="text-2xl font-bold text-gray-900">{totalLosses}</p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <TrendingUp className="h-8 w-8 text-blue-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Win Rate</p>
              <p className="text-2xl font-bold text-gray-900">
                {(winPercentage * 100).toFixed(1)}%
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Teams Grid */}
      {teams.length === 0 ? (
        <div className="card text-center py-12">
          <div className="text-gray-400 mb-4">
            <Users className="h-12 w-12 mx-auto" />
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No teams yet</h3>
          <p className="text-gray-500 mb-4">Connect your fantasy platforms to get started</p>
          <Link to="/settings" className="btn btn-primary">
            <Plus className="h-4 w-4 mr-2" />
            Add Your First Team
          </Link>
        </div>
      ) : (
        <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-6">
          {teams.map((team) => {
            const lineup = teamLineups[team.id];
            const winRate = team.record.wins / (team.record.wins + team.record.losses + team.record.ties);
            
            return (
              <div key={team.id} className="card">
                {/* Team Header */}
                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center">
                    <div className={`w-3 h-3 rounded-full ${getPlatformColor(team.platform)} mr-3`}></div>
                    <div>
                      <h3 className="font-semibold text-gray-900">{team.name}</h3>
                      <p className="text-sm text-gray-600">{team.leagueName}</p>
                    </div>
                  </div>
                  <div className="text-right">
                    <span className={`text-sm font-medium ${
                      winRate >= 0.6 ? 'text-green-600' : 
                      winRate >= 0.4 ? 'text-yellow-600' : 'text-red-600'
                    }`}>
                      {team.record.wins}-{team.record.losses}
                      {team.record.ties > 0 && `-${team.record.ties}`}
                    </span>
                    <p className="text-xs text-gray-500">{team.platform}</p>
                  </div>
                </div>

                {/* Projected Points */}
                {lineup && (
                  <div className="mb-4 p-3 bg-primary-50 rounded-lg">
                    <div className="flex items-center justify-between">
                      <span className="text-sm font-medium text-primary-700">Projected Points</span>
                      <span className="text-lg font-bold text-primary-600">{lineup.totalProjectedPoints}</span>
                    </div>
                  </div>
                )}

                {/* Starting Lineup Grid */}
                {lineup && (
                  <div className="space-y-3">
                    <h4 className="text-sm font-medium text-gray-700">Starting Lineup</h4>
                    <div className="grid grid-cols-2 gap-2">
                      {Object.entries(lineup.players).map(([position, player]) => {
                        if (position === 'BENCH' || !player || Array.isArray(player)) return null;
                        
                        return (
                          <div key={position} className="flex items-center justify-between p-2 bg-gray-50 rounded text-xs">
                            <div className="flex-1 min-w-0">
                              <div className="flex items-center space-x-1">
                                <span className="font-medium text-gray-500">{position}</span>
                                {getInjuryStatus(player)}
                              </div>
                              <p className="font-medium text-gray-900 truncate">{player.name}</p>
                              <p className="text-gray-600">{player.team}</p>
                            </div>
                            {player.projectedPoints && (
                              <span className="text-primary-600 font-medium ml-2">
                                {player.projectedPoints}
                              </span>
                            )}
                          </div>
                        );
                      })}
                    </div>
                  </div>
                )}

                {/* Team Actions */}
                <div className="mt-4 pt-4 border-t border-gray-200">
                  <div className="flex space-x-2">
                    <button
                      onClick={() => syncTeam(team.id)}
                      className="flex-1 btn btn-secondary text-sm"
                      disabled={isLoading}
                    >
                      <RefreshCw className="h-4 w-4 mr-1" />
                      Sync
                    </button>
                    <button className="flex-1 btn btn-primary text-sm">
                      <Edit3 className="h-4 w-4 mr-1" />
                      Edit
                    </button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
